mapper.update:
		UPDATE board
	  	   SET  bo_title = #{bo_title}
         		,bo_content = #{bo_content}
    	 WHERE bo_no = #{bo_no}

update board
set bo_hit = bo_hit + 1
where bo_no = #{bo_no}

		UPDATE notice
	  	   SET  no_bo_title = #{no_bo_title}
         		,no_bo_content = #{no_bo_content}
    	 WHERE no_bo_no = #{no_bo_no}

update notice
set no_bo_hit = no_bo_hit + 1
where no_bo_no = #{no_bo_no}
